import React from "react";
import "./App.scss";
import { connect } from "react-redux";

function App(props) {
  const toggleSharps = () => {
    props.toggle("sharps");
  };

  const toggleHighlightRoots = () => {
    props.toggle("highlightRoots");
  };

  const toggleShowUnusedNotes = () => {
    props.toggle("showUnusedNotes");
  };

  const toggleShowDegrees = () => {
    props.toggle("showDegrees");
  };

  const toggleIndianNotation = () => {
    props.toggle("indianDegreeNotation");
  };

  const toggleInfoModal = () => {
    props.toggle("infoModalOpen");
  };

  return (
    <div>
      <button onClick={toggleSharps}>{props.sharps ? "♭" : "♯"}</button>
      <button onClick={toggleHighlightRoots}>
        {props.highlightRoots ? "true" : "false"}
      </button>
      <button onClick={toggleShowUnusedNotes}>
        {props.showUnusedNotes ? "true" : "false"}
      </button>
      <button onClick={toggleShowDegrees}>
        {props.showDegrees ? "true" : "false"}
      </button>
      <button onClick={toggleIndianNotation}>
        {props.indianDegreeNotation ? "true" : "false"}
      </button>
      <button onClick={toggleInfoModal}>
        {props.infoModalOpen ? "true" : "false"}
      </button>
    </div>
  );
}

function mapStateToProps(state) {
  return {
    sharps: state.sharps,
    highlightRoots: state.highlightRoots,
    showUnusedNotes: state.showUnusedNotes,
    showDegrees: state.showDegrees,
    indianDegreeNotation: state.indianDegreeNotation,
    infoModalOpen: state.infoModalOpen,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    toggle: (name) => dispatch({ type: "TOGGLE", payload: name }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
