import React from "react";
import { connect } from "react-redux";
import String from "../components/String";
import { indices } from "../constants/utils";
import Stepper from "../components/Stepper";
import Fretboard from "../components-jsx/Fretboard";

class FretboardContainer extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      numberOfStrings: 9,
      numberOfFrets: 14,
      currentScale: null
    }
  };



  render() {
    return (
      <Fretboard
        numberOfStrings={this.state.numberOfStrings}
        numberOfFrets={this.state.numberOfFrets}
        currentScale={this.state.currentScale}
      ></Fretboard>
    );
  }

}

function mapStateToProps(state) {
  return {
    numberOfStrings: state.numberOfStrings,
    numberOfFrets: state.numberOfFrets,
  };
}

export default connect(mapStateToProps)(FretboardContainer);
