import React from "react";
import { connect } from "react-redux";
import String from "./String";
import Stepper from "./Stepper";
import { indices } from "../constants/utils";


class Fretboard extends React.Component {

  renderGuitarStrings = () => {
    const strings = [...indices];
    strings.length = this.props.numberOfStrings;
    return strings.map((noteIndex, stringIndex) => {
      return (
        <String
          key={stringIndex}
          rootIndex={noteIndex}
          stringIndex={stringIndex}
          numberOfFrets={this.props.numberOfFrets}
          scale={this.props.currentScale}
        ></String>
      );
    });
  };

  render() {
    return (
      <div className="fretboard">
        <div className="fretboard-options-container">
          <Stepper type="String" />
          <Stepper type="Fret" />
        </div>
        <div className="guitar-string-container">{this.renderGuitarStrings()}</div>
      </div>
    );
  }

}

function mapStateToProps(state) {
  return {
    numberOfStrings: state.numberOfStrings,
    numberOfFrets: state.numberOfFrets,
  };
}

export default connect(mapStateToProps)(Fretboard);
