// renders: strings.
// redux: options for number of strings which each render in that Standard Tuning, and a slider/input for number of frets.

import React from "react";
import { connect } from "react-redux";
import String from "./String";
import scales from "../constants/scales";
import { indices, convertIndexToString } from "../constants/utils";
import FretStepper from "./FretStepper";
import StringStepper from "./StringStepper";
import { render } from "node-sass";

function Fretboard(props) {
  const {
    sharps,
    numberOfStrings,
    numberOfFrets,
    currentScale,
    selectedNoteIndex,
    selectedScaleIndex,
  } = props;

  const renderGuitarStrings = () => {
    const strings = [...indices];
    strings.length = numberOfStrings;
    return strings.map((noteIndex, stringIndex) => {
      return (
        <String
          rootIndex={noteIndex}
          stringIndex={stringIndex}
          numberOfFrets={numberOfFrets}
          scale={currentScale}
        ></String>
      );
    });
  };

  const renderDisplay = () => {
    const name = scales[selectedScaleIndex].name;
    const key = convertIndexToString(selectedNoteIndex, sharps);
    return (
      <div className="current-selection-string">
        `{name + " "} scale in {key}`
      </div>
    );
  };

  return (
    <div className="fretboard">
      <div className="fretboard-options-container">
        <StringStepper></StringStepper>
        {renderDisplay()}
        <FretStepper></FretStepper>
      </div>
      <div className="guitar-string-container">{renderGuitarStrings()}</div>
    </div>
  );
}

function mapStateToProps(state) {
  return {
    sharps: state.sharps,
    numberOfStrings: state.numberOfStrings,
    numberOfFrets: state.numberOfFrets,
    selectedNoteIndex: state.selectedNoteIndex,
    selectedScaleIndex: state.selectedScaleIndex,
  };
}

export default connect(mapStateToProps)(Fretboard);
