import React from "react";
import { connect } from "react-redux";

function FretStepper(props) {
  const { numberOfFrets, setFretCount } = props;
  const decrease = () => {
    if (numberOfFrets !== 12) {
      setFretCount(numberOfFrets - 1);
    }
  };
  const increase = () => {
    if (numberOfFrets !== 24) {
      setFretCount(numberOfFrets + 1);
    }
  };
  const min = (n) => {
    return n === 12 ? "limit" : null;
  };
  const max = (n) => {
    return n === 24 ? "limit" : null;
  };

  return (
    <div className="stepper">
      <button
        id={min(numberOfFrets)}
        onClick={decrease}
        className="plus-or-minus-btn"
      >
        -
      </button>
      <span type="text" id="counter">
        {numberOfFrets.toString()}
      </span>
      <button
        id={max(numberOfFrets)}
        onClick={increase}
        className="plus-or-minus-btn"
      >
        +
      </button>
      <span className="stepper-label" type="text">
        Frets
      </span>
    </div>
  );
}

function mapStateToProps(state) {
  return {
    numberOfFrets: state.numberOfFrets,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setFretCount: (value) =>
      dispatch({ type: "SET_FRET_COUNT", payload: value }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(FretStepper);
