import React from "react";
import { connect } from "react-redux";

const myHeaders = new Headers();
myHeaders.append("Content-Type", "application/json");

const raw = JSON.stringify({
  username: "Admin",
  email: "guitarscalefinder@gmail.com",
  password: "CantAffordSitar",
});

const requestOptions = {
  method: "POST",
  headers: myHeaders,
  body: raw,
  redirect: "follow",
};

function Login(props) {
  const [open, setOpen] = React.useState(false);
  const [email, changeEmail] = React.useState("");
  const [password, changePassword] = React.useState("");

  const toggle = () => {
    setOpen(!open);
  };

  const tryLogin = () => {
    fetch("http://localhost:5000/api/user/login", requestOptions)
      .then((response) => response.text())
      .then((result) => console.log(result))
      .catch((error) => console.log("error", error));
  };

  return (
    <div className="login-root">
      <button className="open-login-button" onClick={toggle}>
        Login to save settings
      </button>

      {open ? (
        <div className="login-modal">
          <form>
            <input
              type="email"
              className="form-control"
              id="email"
              value={email}
              onChange={changeEmail}
              aria-describedby="emailHelp"
              placeholder="enter email"
            />
            <input
              type="password"
              className="form-control"
              value={password}
              onChange={changePassword}
              id="password"
              placeholder="enter password"
            />
          </form>
          <div className="button-container">
            <button type="submit" className="login-button" onClick={tryLogin}>
              Login
            </button>
            <button type="close" className="close-button" onClick={toggle}>
              Close
            </button>
          </div>
        </div>
      ) : null}
    </div>
  );
}

function mapStateToProps(state) {
  return {};
}

function mapDispatchToProps(dispatch) {
  return {
    // loginUser: () =>
    // logOutUser: () => dispatch({ type: "LOG_OUT" }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
