import React from "react";
import { connect } from "react-redux";

function Stepper(props) {
  const { numberOfFrets, setFretCount } = props;
  const min = (n) => {
    n = 12 ? true : false;
  };
  const max = (n) => {
    n = 24 ? true : false;
  };
  const increase = () => {
    setFretCount(max(numberOfFrets + 1) ? 24 : numberOfFrets + 1);
  };
  return <div className="fret-stepper">{numberOfFrets.toString()}</div>;
}

function mapStateToProps(state) {
  return {
    numberOfFrets: state.numberOfFrets,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setFretCount: (value) => dispatch({ type: "FRET", payload: value }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Stepper);
