import React from "react";
import { connect } from "react-redux";

class ScaleTrayContainer extends React.component {
  constructor(props) {
    super(props)
    this.state = {
      selectedScaleIndex: 4
    }
  };

  render() {
    return (
      <ScaleTray
        selectedScaleIndex={this.state.selectedScaleIndex}
      ></ScaleTray>
    )
  }
}

function mapStateToProps(state) {
  return {
    selectedScaleIndex: state.selectedScaleIndex,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setScaleIndex: (value) =>
      dispatch({ type: "SET_SCALE_INDEX", payload: value }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(ScaleTrayContainer);
