import React from "react";
import { connect } from "react-redux";

function Stepper(props) {
  const { numberOfFrets, setFretCount } = props;
  const decrease = () => {
    if (numberOfFrets !== 12) {
      setFretCount(numberOfFrets - 1);
    }
  };
  const increase = () => {
    if (numberOfFrets !== 24) {
      setFretCount(numberOfFrets + 1);
    }
  };
  const min = () => {
    setFretCount(12);
  };
  const max = () => {
    setFretCount(24);
  };

  return (
    <div className="fret-stepper">
      <div className="first-row">
        <button onClick={min}>12</button>
        <span type="text">Frets</span>
        <button onClick={max}>24</button>
      </div>
      <div className="second-row">
        <button onClick={decrease}>-</button>
        {numberOfFrets.toString()}
        <button onClick={increase}>+</button>
      </div>
    </div>
  );
}

function mapStateToProps(state) {
  return {
    numberOfFrets: state.numberOfFrets,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setFretCount: (value) =>
      dispatch({ type: "SET_FRET_COUNT", payload: value }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Stepper);
