import React from "react";
import { connect } from "react-redux";

const myHeaders = new Headers();
myHeaders.append("Content-Type", "application/json");

function Login(props) {
  const {
    highlightRoots,
    sargamNotation,
    sharps,
    degrees,
    showUnusedNotes,
    setNoteIndex,
    setScaleIndex,
    toggle,
    setStringCount,
    setFretCount,
    userPreferences,
  } = props;

  const toggleSharps = () => {
    toggle("sharps");
  };

  const toggleHighlightRoots = () => {
    toggle("highlightRoots");
  };

  const toggleShowUnusedNotes = () => {
    toggle("showUnusedNotes");
  };

  const toggleShowDegrees = () => {
    toggle("degrees");
  };

  const toggleIndianNotation = () => {
    toggle("sargamNotation");
  };

  const setIndex = (note, type);

  const preferences = {
    selectedNoteIndex: props.selectedNoteIndex,
    selectedScaleIndex: props.selectedScaleIndex,
    numberOfStrings: props.numberOfStrings,
    numberOfFrets: props.numberOfFrets,
    highlightRoots: props.highlightRoots,
    showUnusedNotes: props.showUnusedNotes,
    degrees: props.degrees,
    sargamNotation: props.sargamNotation,
    sharps: props.sharps,
  };

  const [open, setOpen] = React.useState(false);
  const [signingUp, toggleSigningUp] = React.useState(false);
  const [username, updateUsername] = React.useState("");
  const [email, updateEmail] = React.useState("");
  const [password, updatePassword] = React.useState("");

  const close = () => {
    setOpen(false);
    toggleSigningUp(false);
    updatePassword("");
  };

  const toggleOpen = () => {
    setOpen(!open);
  };

  const toggleSignUp = () => {
    toggleSigningUp(!signingUp);
  };

  const handleEmailChange = (event) => {
    updateEmail(event.target.value);
  };

  const handleUsernameChange = (event) => {
    updateUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    updatePassword(event.target.value);
  };

  const submitLoginInfo = () => {
    const raw = JSON.stringify({
      username,
      email,
      password,
    });

    const requestOptions = {
      method: "POST",
      headers: myHeaders,
      body: raw,
      redirect: "follow",
    };
    fetch("http://localhost:5000/api/user/login", requestOptions)
      .then((response) => response.text())
      .then((result) => console.log(result))
      .catch((error) => console.log("error", error));
  };

  const signUp = () => {
    const raw = JSON.stringify({
      email,
      password,
      username,
      preferences,
    });

    const requestOptions = {
      method: "POST",
      headers: myHeaders,
      body: raw,
      redirect: "follow",
    };
    fetch("http://localhost:5000/api/user/signup", requestOptions)
      .then((response) => response.text())
      .then((result) => console.log(result))
      .catch((error) => console.log("error", error));
  };

  const mapUserPrefsToState = (savedPrefs) => {
    let newState = {};
    // Copy saved preferences from api
    // Doesn't handle tuning, just defaults it.
    Object.keys(savedPrefs).forEach(function (key) {
      let value = savedPrefs[key];
      newState[key] = value;
    });
    Object.keys(newState).forEach(function (key) {
      let value = newState[key];
      let name = key.toString();
      switch (name) {
        case "selectedNoteIndex":
          setNoteIndex(value);
          break;
        case "selectedScaleIndex":
          setScaleIndex(value);
          break;
        case "numberOfStrings":
          setStringCount(value);
          break;
        case "numberOfFrets":
          setFretCount(value);
          break;
        case "showUnusedNotes":
          if (value !== showUnusedNotes) {
            toggleShowUnusedNotes();
          }
          break;
        case "degrees":
          if (value !== degrees) {
            toggleShowDegrees();
          }
          break;
        case "sargamNotation":
          if (value !== sargamNotation) {
            toggleIndianNotation();
          }
          break;
        case "highlightRoots":
          if (value !== highlightRoots) {
            toggleHighlightRoots();
          }
          break;
        case "sharps":
          if (value !== sharps) {
            toggleSharps();
          }
          break;
        default:
          break;
      }
    });
  };

  // So this works at the most basic level: Can login to admin and console.log success/fail
  // Next: Sign up and Log Out functionality
  // Then: Render frontend from user preferences, and update user preferences onChange ? maybe opposite order ?
  // Then: Session Tokens round 2 to remember the users.

  return (
    <div className="login-root">
      <button className="open-login-button" onClick={toggleOpen}>
        Login or Sign up to save your settings
      </button>
      {open ? (
        <div className="login-modal">
          <form>
            {signingUp ? (
              <input
                id="username"
                value={username}
                onChange={handleUsernameChange}
                placeholder="Username..."
              />
            ) : null}
            <input
              id="email"
              value={email}
              onChange={handleEmailChange}
              placeholder="Email Address..."
            />
            <input
              id="password"
              value={password}
              onChange={handlePasswordChange}
              placeholder="Password..."
            />
          </form>
          {signingUp ? (
            <div className="button-container">
              <button type="submit" className="signup-button" onClick={signUp}>
                Sign up
              </button>
              <button type="close" className="close-button" onClick={close}>
                Close
              </button>
            </div>
          ) : (
            <div className="button-container">
              <button
                type="submit"
                className="login-button"
                onClick={submitLoginInfo}
              >
                Login
              </button>
              {"or"}
              <button
                type="submit"
                className="signup-button"
                onClick={toggleSignUp}
              >
                Sign up
              </button>
              <button type="close" className="close-button" onClick={close}>
                Close
              </button>
            </div>
          )}
        </div>
      ) : null}
    </div>
  );
}

function mapStateToProps(state) {
  return {
    selectedNoteIndex: state.selectedNoteIndex,
    selectedScaleIndex: state.selectedScaleIndex,
    numberOfStrings: state.numberOfStrings,
    numberOfFrets: state.numberOfFrets,
    highlightRoots: state.highlightRoots,
    showUnusedNotes: state.showUnusedNotes,
    degrees: state.degrees,
    sargamNotation: state.sargamNotation,
    sharps: state.sharps,
    userPreferences: state.userPreferences,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    toggle: (name) => dispatch({ type: "TOGGLE", payload: name }),
    setNoteIndex: (index) =>
      dispatch({ type: "SET_NOTE_INDEX", payload: index }),
    setScaleIndex: (index) =>
      dispatch({ type: "SET_SCALE_INDEX", payload: index }),
    setFretCount: (value) =>
      dispatch({ type: "SET_FRET_COUNT", payload: value }),
    setStringCount: (value) =>
      dispatch({ type: "SET_STRING_COUNT", payload: value }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
