// eslint-disable-next-line
import React from "react";
import { connect } from "react-redux";

const myHeaders = new Headers();
myHeaders.append("Content-Type", "application/json");

function SignUpForm(props) {
  const { isOpen, u, e, p } = props;

  const [open, setOpen] = React.useState(isOpen);
  const [username, updateUsername] = React.useState(u);
  const [email, updateEmail] = React.useState(e);
  const [password, updatePassword] = React.useState(p);

  const handleUsernameChange = (event) => {
    updateUsername(event.target.value);
  };

  const handleEmailChange = (event) => {
    updateEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    updatePassword(event.target.value);
  };

  const toggle = () => {
    setOpen(!open);
  };

  const signUp = () => {
    const raw = JSON.stringify({
      email,
      password,
      username,
    });

    const requestOptions = {
      method: "POST",
      headers: myHeaders,
      body: raw,
      redirect: "follow",
    };
    fetch("http://localhost:5000/api/user/signup", requestOptions)
      .then((response) => response.text())
      .then((result) => console.log(result))
      .catch((error) => console.log("error", error));
  };

  return (
    <div className="signup-root">
      <button className="open-signup-button" onClick={toggle}>
        Sign up
      </button>
      <div className="signup-modal">
        (
        <div className="signup-form-wrapper">
          <form>
            <input
              id="username"
              value={username}
              onChange={handleUsernameChange}
            />
            <input id="email" value={email} onChange={handleEmailChange} />
            <input
              id="password"
              value={password}
              onChange={handlePasswordChange}
            />
          </form>
          <div className="button-container">
            <button type="submit" className="signup-button" onClick={signUp}>
              Sign Up
            </button>
            {"or"}
            <button type="close" className="close-button" onClick={toggle}>
              Cancel
            </button>
          </div>
        </div>
      </div>
      ) : null}
    </div>
  );
}

function mapStateToProps(state) {
  return {};
}

export default connect(mapStateToProps)(SignUpForm);
