import React from "react";
import { connect } from "react-redux";

function Login(props) {
  const [open, setOpen] = React.useState(false);

  const [email, updateEmail] = React.useState("");
  const [password] = React.useState("");

  const toggle = () => {
    setOpen(!open);
  };

  const handleEmailChange = (value) => {
    console.log(value);
    updateEmail(value.toString());
  };

  const submitLoginInfo = () => {
    const myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    const raw = JSON.stringify({
      email,
      password,
    });

    const requestOptions = {
      method: "POST",
      headers: myHeaders,
      body: raw,
      redirect: "follow",
    };
    fetch("http://localhost:5000/api/user/login", requestOptions)
      .then((response) => response.text())
      .then((result) => console.log(result))
      .catch((error) => console.log("error", error));
  };

  return (
    <div className="login-root">
      <button className="open-login-button" onClick={toggle}>
        Login to save settings
      </button>

      {open ? (
        <div className="login-modal">
          <form>
            <input id="email" value={email} onChange={handleEmailChange} />
            <input id="password" value={password} />
          </form>
          <div className="button-container">
            <button
              type="submit"
              className="login-button"
              onClick={submitLoginInfo}
            >
              Login
            </button>
            <button type="close" className="close-button" onClick={toggle}>
              Close
            </button>
          </div>
        </div>
      ) : null}
    </div>
  );
}

function mapStateToProps(state) {
  return {};
}

function mapDispatchToProps(dispatch) {
  return {
    // loginUser: () =>
    // logOutUser: () => dispatch({ type: "LOG_OUT" }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
