// eslint-disable-next-line
import React, { useState } from "react";
import { connect } from "react-redux";
import SignUpForm from "./SignUpForm";

// function LogOut , a dumb component that displays name and gives logout button when !open && userID is real

function Login(props) {
  const [open, setOpen] = React.useState(false);

  const toggle = () => {
    setOpen(!open);
  };
  return (
    <div className="login-form-modal">
      {!open ? (
        <button className="open-login-button" onClick={toggle}>
          Login to save settings
        </button>
      ) : null}
      {open ? (
        <div className="login-form-container">
          <form>
            <input
              type="email"
              className="form-control"
              id="email"
              aria-describedby="emailHelp"
              placeholder="Enter email"
            />
            <input
              type="password"
              className="form-control"
              id="password"
              placeholder="Password"
            />
          </form>
          <div className="button-container">
            <button type="submit" className="login-button">
              Login
            </button>
            {"or"}
            <div type="submit" className="signUp-div">
              <SignUpForm></SignUpForm>
            </div>
            <button type="close" className="close-button" onClick={toggle}>
              Close
            </button>
          </div>
        </div>
      ) : null}
    </div>
  );
}

function mapStateToProps(state) {
  return {
    userID: state.userID,
  };
}

export default connect(mapStateToProps)(Login);
