import React from "react";
import ScaleTray from "../components-jsx/ScaleTray";
import { connect } from "react-redux";

class ScaleTrayContainer extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      selectedScaleIndex: 4
    }
  };

  setToMajor = () => {
    this.props.setScaleIndex(0);
    alert('xd!');
  }

  render() {
    return (
      <ScaleTray
        setToMajor={this.setToMajor}
        selectedScaleIndex={this.state.selectedScaleIndex}
      ></ScaleTray>
    );
  }
}

function mapStateToProps(state) {
  return {
    selectedScaleIndex: state.selectedScaleIndex,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setScaleIndex: (value) =>
      dispatch({ type: "SET_SCALE_INDEX", payload: value }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(ScaleTrayContainer);
