import React from "react";
import "./App.scss";
import { connect } from "react-redux";
import Fretboard from "./components/Fretboard";
import scales from "./constants/scales";
import {
  convertIndexToString,
  getNotation,
  createScale,
} from "./constants/utils";

function App(props) {
  const toggleSharps = () => {
    props.toggle("sharps");
  };

  const toggleHighlightRoots = () => {
    props.toggle("highlightRoots");
  };

  const toggleShowUnusedNotes = () => {
    props.toggle("showUnusedNotes");
  };

  const toggleShowDegrees = () => {
    props.toggle("showDegrees");
  };

  const toggleIndianNotation = () => {
    props.toggle("indianDegreeNotation");
  };

  const toggleInfoModal = () => {
    props.toggle("infoModalOpen");
  };

  const toggleOptions = {
    sharps: {
      toggle: toggleSharps,
      btnText: props.sharps ? "♭" : "♯",
      round: true,
    },
    highlightRoots: {
      toggle: toggleHighlightRoots,
      btnText: "Highlight roots",
      round: false,
    },
    showUnusedNotes: {
      toggle: toggleShowUnusedNotes,
      btnText: props.showUnusedNotes ? "Label scale only" : "Label all notes",
      round: false,
    },
    showDegrees: {
      toggle: toggleShowDegrees,
      btnText: props.showDegrees ? "Hide degrees" : "Show degrees",
      round: false,
    },
    infoModal: {
      toggle: toggleInfoModal,
      btnText: "info",
      round: true,
    },
  };

  randomize = () => {
    this.props.setNoteIndex(Math.floor(Math.random() * 12));
    this.props.setScaleIndex(Math.floor(Math.random() * scales.length));
  };

  renderNoteButtons = (notation) => {
    return notation.map((note, idx) => {
      return (
        <button
          key={[note, idx]}
          className="round-button-purple"
          id={idx === this.props.selectedNoteIndex ? "selected" : null}
          onClick={() => {
            this.props.setNoteIndex(idx);
          }}
        >
          {note}
        </button>
      );
    });
  };

  renderToggleButtons = () => {
    return toggleOptions.map((note, idx) => {
      return (
        <button
          key={[note, idx]}
          className="round-button-purple"
          id={idx === this.props.selectedNoteIndex ? "selected" : null}
          onClick={() => {
            this.props.setNoteIndex(idx);
          }}
        >
          {note}
        </button>
      );
    });
  };

  const rootDisplay = convertIndexToString(selectedNoteIndex, sharps);
  const scaleDisplay = scales[selectedScaleIndex].name;
  const currentScale = createScale(
    selectedNoteIndex,
    scales[selectedScaleIndex].pattern
  );
  const notation = getNotation(sharps);

  return (
    <div className="App">
      <div className="toggle-button-container">
        <button onClick={toggleSharps}>{props.sharps ? "♭" : "♯"}</button>
        <button onClick={toggleHighlightRoots}>
          {props.highlightRoots ? "true" : "false"}
        </button>
        <button onClick={toggleShowUnusedNotes}>
          {props.showUnusedNotes ? "true" : "false"}
        </button>
        <button onClick={toggleShowDegrees}>
          {props.showDegrees ? "true" : "false"}
        </button>
        <button onClick={toggleIndianNotation}>
          {props.indianDegreeNotation ? "true" : "false"}
        </button>
        <button onClick={toggleInfoModal}>
          {props.infoModalOpen ? "true" : "false"}
        </button>
      </div>
      <div className="note-button-container">{renderNoteButtons(notation)}</div>
      {/* scale selectors */}
      <Fretboard></Fretboard>
    </div>
  );
}

function mapStateToProps(state) {
  return {
    sharps: state.sharps,
    highlightRoots: state.highlightRoots,
    showUnusedNotes: state.showUnusedNotes,
    showDegrees: state.showDegrees,
    indianDegreeNotation: state.indianDegreeNotation,
    infoModalOpen: state.infoModalOpen,
    selectedNoteIndex: state.selectedNoteIndex,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    toggle: (name) => dispatch({ type: "TOGGLE", payload: name }),
    setNoteIndex: (name) => dispatch({ type: "TOGGLE", payload: name }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
