import React from "react";
import { connect } from "react-redux";
import SignUpForm from "./SignUpForm";

const myHeaders = new Headers();
myHeaders.append("Content-Type", "application/json");

function Login(props) {
  const [open, setOpen] = React.useState(false);

  const [signingUp, toggleSigningUp] = React.useState(false);

  const [username, updateUsername] = React.useState("");
  const [email, updateEmail] = React.useState("");
  const [password, updatePassword] = React.useState("");

  const toggle = () => {
    setOpen(!open);
  };

  const toggleSignUp = () => {
    toggleSigningUp(!signingUp);
  };

  const handleEmailChange = (event) => {
    updateEmail(event.target.value);
  };

  const handleUsernameChange = (event) => {
    updateUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    updatePassword(event.target.value);
  };

  const submitLoginInfo = () => {
    const raw = JSON.stringify({
      username,
      email,
      password,
    });

    const requestOptions = {
      method: "POST",
      headers: myHeaders,
      body: raw,
      redirect: "follow",
    };
    fetch("http://localhost:5000/api/user/login", requestOptions)
      .then((response) => response.text())
      .then((result) => console.log(result))
      .catch((error) => console.log("error", error));
  };

  const signUp = () => {
    const raw = JSON.stringify({
      email,
      password,
      username,
    });

    const requestOptions = {
      method: "POST",
      headers: myHeaders,
      body: raw,
      redirect: "follow",
    };
    fetch("http://localhost:5000/api/user/signup", requestOptions)
      .then((response) => response.text())
      .then((result) => console.log(result))
      .catch((error) => console.log("error", error));
  };

  // So this works at the most basic level: Can login to admin and console.log success/fail
  // Next: Sign up and Log Out functionality
  // Then: Render frontend from user preferences, and update user preferences onChange ? maybe opposite order ?
  // Then: Session Tokens round 2 to remember the users.

  return (
    <div className="login-root">
      <button className="open-login-button" onClick={toggle}>
        Login or Sign up to save your settings
      </button>
      {open ? (
        <div className="login-modal">
          <form>
            {signingUp ? (
              <input
                id="username"
                value={username}
                onChange={handleUsernameChange}
              />
            ) : null}
            <input id="email" value={email} onChange={handleEmailChange} />
            <input
              id="password"
              value={password}
              onChange={handlePasswordChange}
            />
          </form>
          {signingUp ? (
            <div className="button-container">
              <button
                type="submit"
                className="signup-button"
                onClick={toggleSignUp}
              >
                Sign up
              </button>
              <button type="close" className="close-button" onClick={toggle}>
                Close
              </button>
            </div>
          ) : (
            <div className="button-container">
              <button
                type="submit"
                className="login-button"
                onClick={submitLoginInfo}
              >
                Login
              </button>
              {"or"}
              <button
                type="submit"
                className="signup-button"
                onClick={toggleSignUp}
              >
                Sign up
              </button>
              <button type="close" className="close-button" onClick={toggle}>
                Close
              </button>
            </div>
          )}
        </div>
      ) : null}
    </div>
  );
}

function mapStateToProps(state) {
  return {};
}

function mapDispatchToProps(dispatch) {
  return {
    // loginUser: () =>
    // logOutUser: () => dispatch({ type: "LOG_OUT" }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
