// need to make generic dropdown that takes an array and connects to the right store
// use same dropdown for: scales, preset tunings, number of strings, possibly even notes depending on what ui looks like

import React from "react";
import { connect } from "react-redux";

function Dropdown(props) {
  const { options, role } = props;
  const renderOptions = () => {
    return (
      <ul className="dropdown-option-container">
        {options.map((item, idx) => {
          return (
            <li className="dropdown-option-wrapper">
              <button
                className="dropdown-option-button"
                onClick={handleChange(idx)}
              >
                {item.name}
              </button>
            </li>
          );
        })}
      </ul>
    );
  };

  const handleChange = (selection) => {
    if (role === "scale-selector") {
      props.setScaleIndex(selection);
      handleClose();
    }
  };

  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(!open);
  };

  const handleClose = () => {
    setOpen(false);
    // handleChange rops. pass 'role' etc. code roles 1 @ a time
  };

  return (
    <div className="dropdown">
      <button className="dropdown-open-button" onClick={handleOpen}>
        open the dropdown / display current selection
      </button>
      {open ? (
        <div
          className="modal"
          open={open}
          onClose={handleClose}
          onClick={handleClose}
        >
          {renderOptions()}
        </div>
      ) : null}
    </div>
  );
}

function mapStateToProps(state) {
  return {
    selectedNoteIndex: state.selectedNoteIndex,
    selectedScaleIndex: state.selectedScaleIndex,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setNoteIndex: (value) =>
      dispatch({ type: "SET_NOTE_INDEX", payload: value }),
    setScaleIndex: (value) =>
      dispatch({ type: "SET_SCALE_INDEX", payload: value }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Dropdown);
