// renders: strings.
// redux: options for number of strings which each render in that Standard Tuning, and a slider/input for number of frets.

import React from "react";
import { connect } from "react-redux";
import String from "./String";
import NumStringsDropdown from "./NumStringsDropdown";
import stringOptions from "../constants/stringOptions";
import FretStepper from "./FretStepper";
import StringStepper from "./StringStepper";

function Fretboard(props) {
  const { numberOfStrings, numberOfFrets, currentScale } = props;

  const retrieveIndices = (value) => {
    if (value === 6) {
      return [...stringOptions[0].indices];
    } else if (value === 7) {
      return [...stringOptions[1].indices];
    } else if (value === 8) {
      return [...stringOptions[2].indices];
    }
  };
  const renderGuitarStrings = () => {
    const strings = retrieveIndices(numberOfStrings);
    return strings.map((noteIndex, stringIndex) => {
      return (
        <String
          rootIndex={noteIndex}
          stringIndex={stringIndex}
          numberOfFrets={numberOfFrets}
          scale={currentScale}
        ></String>
      );
    });
  };

  return (
    <div className="fretboard">
      <div className="fretboard-options-container">
        <NumStringsDropdown options={stringOptions} />
        <StringStepper></StringStepper>
        <FretStepper></FretStepper>
      </div>
      <div className="guitar-string-container">{renderGuitarStrings()}</div>
    </div>
  );
}

function mapStateToProps(state) {
  return {
    numberOfStrings: state.numberOfStrings,
    numberOfFrets: state.numberOfFrets,
  };
}

export default connect(mapStateToProps)(Fretboard);
