import React from "react";
import { connect } from "react-redux";

function FretStepper(props) {
  const { numberOfFrets, setFretCount } = props;
  const decrease = () => {
    if (numberOfFrets !== 12) {
      setFretCount(numberOfFrets - 1);
    }
  };
  const increase = () => {
    if (numberOfFrets !== 24) {
      setFretCount(numberOfFrets + 1);
    }
  };
  const min = () => {
    setFretCount(12);
  };
  const max = () => {
    setFretCount(24);
  };

  return (
    <div className="stepper">
      <button onClick={min} className="gray-round-btn">
        12
      </button>
      <button onClick={decrease} className="gray-round-btn">
        -
      </button>
      <span type="text" id="counter">
        {numberOfFrets.toString()}
      </span>
      <span type="text">Frets</span>
      <button onClick={increase} className="gray-round-btn">
        +
      </button>
      <button onClick={max} className="gray-round-btn">
        24
      </button>
    </div>
  );
}

function mapStateToProps(state) {
  return {
    numberOfFrets: state.numberOfFrets,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setFretCount: (value) =>
      dispatch({ type: "SET_FRET_COUNT", payload: value }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(FretStepper);
