// renders number of frets from store; with root note based on standard tuning for number of strings
// has state and can change its own tuning; marker will indicate custom tuning

import React from "react";
import Fret from "./Fret";

function String(props) {
  // react.useState for a tuning value (default props.root), then buttons to go up and down 1 step at a down. need parser func

  const tuneUp = () => {};

  const tuneDown = () => {};

  const renderFrets = () => {
    // array.keys() from props.numberOfFrets
    let arr = [1, 2];
    return arr.map((noteIndex) => {
      return <Fret noteIndex={noteIndex}></Fret>;
    });
  };

  // componentDidUpdate for changeTuningRequest eventually

  return (
    <div>
      <p>this guitar string is rendering frets</p>
    </div>
  );
}

export default String;
