import React from "react";
import { connect } from "react-redux";

function StringStepper(props) {
  const { numberOfStrings, setStringCount } = props;
  const decrease = () => {
    if (numberOfStrings !== 4) {
      setStringCount(numberOfStrings - 1);
    }
  };
  const increase = () => {
    if (numberOfStrings !== 12) {
      setStringCount(numberOfStrings + 1);
    }
  };
  const min = () => {
    setStringCount(4);
  };
  const max = () => {
    setStringCount(12);
  };

  return (
    <div className="stepper">
      <button onClick={min} className="gray-round-btn">
        4
      </button>
      <button onClick={decrease} className="gray-round-btn">
        -
      </button>
      <span type="text" id="counter">
        {numberOfStrings.toString()}
      </span>
      <span type="text">Strings</span>
      <button onClick={increase} className="gray-round-btn">
        +
      </button>
      <button onClick={max} className="gray-round-btn">
        12
      </button>
    </div>
  );
}

function mapStateToProps(state) {
  return {
    numberOfStrings: state.numberOfStrings,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setStringCount: (value) =>
      dispatch({ type: "SET_STRING_COUNT", payload: value }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(StringStepper);
