// renders: strings.
// redux: options for number of strings which each render in that Standard Tuning, and a slider/input for number of frets.

import React from "react";
import { connect } from "react-redux";
import String from "./String";

function Fretboard(props) {
  // react.useState for a value (default 24), then a slider for number of frets.
  // strings will be dropdown menu , rendered from a const file

  const changeNumberOfStrings = () => {
    props.changeNumberOfStrings(4);
  };

  const changeNumberOfFrets = () => {
    props.changeNumberOfFrets(12);
  };

  const renderGuitarStrings = () => {
    // get tuning array for that number
    let arr = [0, 1, 2, 3, 4, 5];
    return arr.map((noteIndex, stringIndex) => {
      return (
        <String
          rootIndex={noteIndex}
          stringIndex={stringIndex}
          numberOfFrets={props.numberOfFrets}
        ></String>
      );
    });
  };

  return (
    <div>
      <button onClick={changeNumberOfStrings}>test strings</button>
      <button onClick={changeNumberOfFrets}>test frets</button>
      <p>{props.numberOfStrings.toString()}</p>
      <p>{props.numberOfFrets.toString()}</p>
    </div>
  );
}

function mapStateToProps(state) {
  return {
    numberOfStrings: state.numberOfStrings,
    numberOfFrets: state.numberOfFrets,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    changeNumberOfFrets: (value) =>
      dispatch({ type: "SET_FRET_COUNT", payload: value }),
    changeNumberOfStrings: (value) =>
      dispatch({ type: "SET_STRING_COUNT", payload: value }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Fretboard);
