import React from "react";
import "./App.scss";
import { connect } from "react-redux";
import Fretboard from "./components/Fretboard";
import ScaleDropdown from "./components/ScaleDropdown";
import NumStringsDropdown from "./components/NumStringsDropdown";
import scales from "./constants/scales";
import {
  convertIndexToString,
  getNotation,
  createScale,
} from "./constants/utils";
import stringOptions from "./constants/stringOptions";

function App(props) {
  // css changes will be made based on the ones in const - no need to get all
  const {
    sharps,
    highlightRoots,
    indianDegreeNotation,
    selectedNoteIndex,
    selectedScaleIndex,
  } = props;
  const toggleSharps = () => {
    props.toggle("sharps");
  };

  const toggleHighlightRoots = () => {
    props.toggle("highlightRoots");
  };

  const toggleShowUnusedNotes = () => {
    props.toggle("showUnusedNotes");
  };

  const toggleShowDegrees = () => {
    props.toggle("showDegrees");
  };

  const toggleIndianNotation = () => {
    props.toggle("indianDegreeNotation");
  };

  const toggleInfoModal = () => {
    props.toggle("infoModalOpen");
  };

  const toggleOptions = [
    {
      toggle: toggleSharps,
      btnText: props.sharps ? "♭" : "♯",
      round: true,
    },
    {
      toggle: toggleHighlightRoots,
      btnText: "Highlight roots",
      round: false,
    },
    {
      toggle: toggleShowUnusedNotes,
      btnText: props.showUnusedNotes ? "Label scale only" : "Label all notes",
      round: false,
    },
    {
      toggle: toggleShowDegrees,
      btnText: props.showDegrees ? "Hide degrees" : "Show degrees",
      round: false,
    },
    {
      toggle: toggleInfoModal,
      btnText: "info",
      round: true,
    },
  ];

  const randomize = () => {
    props.setNoteIndex(Math.floor(Math.random() * 12));
    props.setScaleIndex(Math.floor(Math.random() * scales.length));
  };

  const renderNoteButtons = (notation) => {
    return notation.map((note, idx) => {
      return (
        <button
          key={[note, idx]}
          className="round-button-purple"
          id={idx === selectedNoteIndex ? "selected" : null}
          onClick={() => {
            props.setNoteIndex(idx);
          }}
        >
          {note}
        </button>
      );
    });
  };

  const renderToggleButtons = () => {
    return toggleOptions.map((btn, idx) => {
      return (
        <button
          key={idx}
          className={
            btn.round ? "round-button-gray" : "rectangular-button-gray"
          }
          onClick={btn.toggle}
        >
          {btn.btnText}
        </button>
      );
    });
  };

  const rootDisplay = convertIndexToString(selectedNoteIndex, sharps);
  const scaleDisplay = scales[selectedScaleIndex].name;
  const currentScale = createScale(
    selectedNoteIndex,
    scales[selectedScaleIndex].pattern
  );
  const notation = getNotation(sharps);

  return (
    <div className="App">
      <div className="top-card">
        <div className="note-button-container">
          {renderNoteButtons(notation)}
        </div>
        {/* scale selector, randomize button and link to App Store */}
        <div className="toggle-button-container">
          {renderToggleButtons()}
          {/* when showDegrees is on, render a small option to change between numbers and indian swaras */}
        </div>
      </div>
      <div className="main-display">
        <div className="current-selection-string">
          <h4>{rootDisplay}</h4>
          <h4>{scaleDisplay}</h4>
        </div>

        <NumStringsDropdown options={stringOptions}></NumStringsDropdown>
        <Fretboard currentScale={currentScale}></Fretboard>
        <ScaleDropdown options={scales}></ScaleDropdown>
      </div>
    </div>
  );
}

function mapStateToProps(state) {
  return {
    sharps: state.sharps,
    highlightRoots: state.highlightRoots,
    showUnusedNotes: state.showUnusedNotes,
    showDegrees: state.showDegrees,
    indianDegreeNotation: state.indianDegreeNotation,
    infoModalOpen: state.infoModalOpen,
    selectedNoteIndex: state.selectedNoteIndex,
    selectedScaleIndex: state.selectedScaleIndex,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    toggle: (name) => dispatch({ type: "TOGGLE", payload: name }),
    setNoteIndex: (index) =>
      dispatch({ type: "SET_NOTE_INDEX", payload: index }),
    setScaleIndex: (index) =>
      dispatch({ type: "SET_SCALE_INDEX", payload: index }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
