import React from "react";
import { connect } from "react-redux";
import { convertIndexToString } from "../constants/utils";

function Fret(props) {
  const { note, sharps } = props;

  return (
    <div className="fret" id="determined by props">
      <p className="fret-text">{convertIndexToString(note, sharps)}</p>
      {/* swaras, degrees */}
    </div>
  );
}

function mapStateToProps(state) {
  return {
    sharps: state.sharps,
    // dbl check names
    // showUnusedNotes
    //         highlightRoots
    //         degrees
    //         sharps
    //         indianDegreeNotation
  };
}

export default connect(mapStateToProps)(Fret);
