import React from "react";
import { connect } from "react-redux";

function Stepper(props) {
  const { numberOfFrets, setFretCount } = props;
  const decrease = () => {
    if (numberOfFrets !== 12) {
      setFretCount(numberOfFrets - 1);
    }
  };
  const increase = () => {
    numberOfFrets === 24 ? null : setFretCount(numberOfFrets + 1);
  };
  return (
    <div className="fret-stepper">
      <button onClick={decrease}>-</button>
      {numberOfFrets.toString()}
      <button onClick={increase}>+</button>
    </div>
  );
}

function mapStateToProps(state) {
  return {
    numberOfFrets: state.numberOfFrets,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setFretCount: (value) =>
      dispatch({ type: "SET_FRET_COUNT", payload: value }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Stepper);
