import React from "react";
import { connect } from "react-redux";

function Stepper(props) {
  const { numberOfFrets, setFretCount } = props;
  const min = (n) => {
    n = 12 ? true : false;
  };
  const max = (n) => {
    n = 24 ? true : false;
  };
  const decrease = () => {
    min(numberOfFrets) ? setFretCount(12) : setFretCount(numberOfFrets - 1);
  };
  const increase = () => {
    max(numberOfFrets) ? setFretCount(24) : setFretCount(numberOfFrets + 1);
  };
  return (
    <div className="fret-stepper">
      <button onClick={decrease}>-</button>
      {numberOfFrets.toString()}
      <button onClick={increase}>+</button>
    </div>
  );
}

function mapStateToProps(state) {
  return {
    numberOfFrets: state.numberOfFrets,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setFretCount: (value) =>
      dispatch({ type: "SET_FRET_COUNT", payload: value }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Stepper);
