// renders number of frets from store; with root note based on standard tuning for number of strings
// has state and can change its own tuning; marker will indicate custom tuning

import React from "react";
import Fret from "./Fret";
import { convertIndexToString, parse } from "../constants/utils";
import { connect } from "redux";

function String(props) {
  const { numberOfFrets, rootIndex, stringIndex, scale, sharps } = props;
  // react.useState for a tuning value (default props.root), then buttons to go up and down 1 step at a down. import * parser func

  const tuneUp = () => {};

  const tuneDown = () => {};

  const renderFrets = () => {
    const frets = [...Array(numberOfFrets).keys()];
    const fretLabels = [1, 3, 5, 7, 9, 12, 15, 17, 19, 21, 24];
    return frets.map((value, idx) => {
      const fretNumber = idx + 1;
      const fretNote = parse(rootIndex + fretNumber);
      // instead of rootIndex, parse will take state.root or whatever its called once tuning is in
      let label;
      if (fretLabels.includes(fretNumber)) {
        label = fretNumber;
      }
      return (
        <React.Fragment key={[value, idx]}>
          <Fret
            note={fretNote}
            scale={scale}
            label={
              stringIndex === 5 && fretLabels.includes(fretNumber)
                ? label
                : null
            }
          ></Fret>
        </React.Fragment>
      );
    });
  };

  // componentDidUpdate for changeTuningRequest eventually

  return (
    <div className="guitar-string">
      <p>{convertIndexToString(rootIndex, sharps)}</p>
      {renderFrets()}
    </div>
  );
}

function mapStateToProps(state) {
  return {
    sharps: state.sharps,
  };
}

export default connect(mapStateToProps)(String);
