import React from "react";
import { connect } from "react-redux";

function Login(props) {
  const [open, setOpen] = React.useState(false);

  const [emailInput, setEmail] = React.useState("");
  const [passwordInput, setPassword] = React.useState("");

  const toggle = () => {
    setOpen(!open);
  };

  const handleInputChange = (name, value) => {
    if (name === emailInput) {
      setEmail(value);
    } else if (name === passwordInput) {
      setPassword(value);
    }
    console.log(emailInput, passwordInput);
  };

  const submitLoginInfo = () => {
    const myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    const raw = JSON.stringify({
      email: { emailInput },
      password: { passwordInput },
    });

    const requestOptions = {
      method: "POST",
      headers: myHeaders,
      body: raw,
      redirect: "follow",
    };
    fetch("http://localhost:5000/api/user/login", requestOptions)
      .then((response) => response.text())
      .then((result) => console.log(result))
      .catch((error) => console.log("error", error));
  };

  return (
    <div className="login-root">
      <button className="open-login-button" onClick={toggle}>
        Login to save settings
      </button>

      {open ? (
        <div className="login-modal">
          <form>
            <input
              type="email"
              className="form-control"
              id="email"
              value={emailInput}
              aria-describedby="emailHelp"
              placeholder="enter email"
              onChange={handleInputChange}
            />
            <input
              type="password"
              className="form-control"
              id="password"
              value={passwordInput}
              placeholder="enter password"
              onChange={handleInputChange}
            />
          </form>
          <div className="button-container">
            <button
              type="submit"
              className="login-button"
              onClick={submitLoginInfo}
            >
              Login
            </button>
            <button type="close" className="close-button" onClick={toggle}>
              Close
            </button>
          </div>
        </div>
      ) : null}
    </div>
  );
}

function mapStateToProps(state) {
  return {};
}

function mapDispatchToProps(dispatch) {
  return {
    // loginUser: () =>
    // logOutUser: () => dispatch({ type: "LOG_OUT" }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
