// renders: strings.
// redux: options for number of strings which each render in that Standard Tuning, and a slider/input for number of frets.

import React from "react";
import { connect } from "react-redux";
import String from "./String";
import stringOptions from "../constants/stringOptions";

function Fretboard(props) {
  // react.useState for a value (default 24), then a slider for number of frets.
  // # of strings will be <dropdown> menu , rendered from stringOptions file

  const changeNumberOfStrings = () => {
    props.changeNumberOfStrings(7);
  };

  const changeNumberOfFrets = () => {
    props.changeNumberOfFrets(12);
  };

  const renderGuitarStrings = () => {
    // get tuning array for that number
    let strings = [...stringOptions[0].indices];
    if (props.numberOfStrings === 7) {
      strings = [...stringOptions[1].indices];
    }
    return strings.map((noteIndex, stringIndex) => {
      return (
        <String
          rootIndex={noteIndex}
          stringIndex={stringIndex}
          numberOfFrets={props.numberOfFrets}
        ></String>
      );
    });
  };

  return (
    <div className="fretboard">
      <div className="fretboard-options-container">
        <button onClick={changeNumberOfStrings}>
          Number of strings: {props.numberOfStrings}
        </button>
        <button onClick={changeNumberOfFrets}>
          Number of frets: {props.numberOfFrets}
        </button>
      </div>
      <div className="guitar-string-container">{renderGuitarStrings()}</div>
    </div>
  );
}

function mapStateToProps(state) {
  return {
    numberOfStrings: state.numberOfStrings,
    numberOfFrets: state.numberOfFrets,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    changeNumberOfFrets: (value) =>
      dispatch({ type: "SET_FRET_COUNT", payload: value }),
    changeNumberOfStrings: (value) =>
      dispatch({ type: "SET_STRING_COUNT", payload: value }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Fretboard);
