// renders number of frets from store; with root note based on standard tuning for number of strings
// has state and can change its own tuning; marker will indicate custom tuning

import React from "react";
import Fret from "./Fret";
import { parse } from "../constants/utils";

function String(props) {
  const { numberOfFrets, rootIndex, scale } = props;
  // react.useState for a tuning value (default props.root), then buttons to go up and down 1 step at a down. import * parser func

  const tuneUp = () => {};

  const tuneDown = () => {};

  renderFrets = (root) => {
    const frets = [...Array(24).keys()];
    const fretLabels = [1, 3, 5, 7, 9, 12, 15, 17, 19, 21, 24];
    return frets.map((value, idx) => {
      const fretNumber = idx + 1;
      const fretNote = parse(root + fretNumber);
      let label;
      if (fretLabels.includes(fretNumber)) {
        label = fretNumber;
      }
      return (
        <React.Fragment key={[value, idx]}>
          <Fret
            note={fretNote}
            showUnusedNotes={this.props.showUnusedNotes}
            highlightRoots={this.props.highlightRoots}
            degrees={this.props.degrees}
            sharps={this.props.sharps}
            selectedScale={this.props.selectedScale}
            label={
              this.props.stringIndex === 5 && fretLabels.includes(fretNumber)
                ? label
                : null
            }
          ></Fret>
        </React.Fragment>
      );
    });
  };

  // componentDidUpdate for changeTuningRequest eventually

  return <div className="guitar-string">{renderFrets()}</div>;
}

export default String;
