// need to make generic dropdown that takes an array and connects to the right store
// use same dropdown for: scales, preset tunings, number of strings, possibly even notes depending on what ui looks like

import React from "react";
import { connect } from "react-redux";

function Dropdown(props) {
  if (props.role === "scale-selector") {
    const handleChange = (selection) => {
      props.setScaleIndex(selection);
    };
  }
  // depending on props.type or props.role ("note-selector", or "scale-selector" etc), get Options
  // state/store/props need to be handled onChange for selected value (note index, scale index, num of strings etc)
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(!open);
  };

  const handleClose = () => {
    setOpen(false);
    // handleChange rops. pass 'role' etc. code roles 1 @ a time
  };

  return (
    <div className="dropdown">
      <button onClick={handleOpen}>
        open the dropdown / display current selection
      </button>
      {open ? (
        <div
          className="modal"
          open={open}
          onClose={handleClose}
          onClick={handleClose}
        >
          <p>the options will render here</p>
        </div>
      ) : null}
    </div>
  );
}

function mapStateToProps(state) {
  return {
    selectedNoteIndex: state.selectedNoteIndex,
    selectedScaleIndex: state.selectedScaleIndex,
    // map to all, but only change (call dispatch) based on type?
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setNoteIndex: (value) =>
      dispatch({ type: "SET_NOTE_INDEX", payload: value }),
    setScaleIndex: (value) =>
      dispatch({ type: "SET_SCALE_INDEX", payload: value }),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Dropdown);
